#!/usr/bin/env python
#coding=utf8
"""
Use toilet & cowsay
"""
import os
import subprocess

def get_font_list():
    """
    return a list of available figlet fonts
    """
    font_options = os.listdir('/usr/share/figlet')
    font_options = [line for line in font_options if not line.endswith('.flc')]
    font_options.sort()
    return font_options

class Figlet():
    """
    Work with 'toilet' or 'figlet' commands.
    """
    def __init__(self, font='ascii9', color=None):
        self.font = font
        self.color = '' if not color else color

    def __str__(self):
        command = 'toilet -F border -f {} --{} '.format(self.font, self.color)
        return command

    def output(self, text, get_str=False):
        """
        output from toilet/figlet
        """
        art_str = subprocess.check_output(
            f'{self.__str__()}{text}', shell=True).decode('utf8')
        if get_str:
            return art_str
        print(art_str)
        return None
